from fasthtml.common import *
from dataclasses import dataclass
from datetime import date

@dataclass
class Card:
    id: int = None
    front: str = ''
    back: str = ''
    next_review: str = ''
    interval: int = 1

db = database('data/cards.db')
cards = db.create(Card)

app, rt = fast_app()

@rt('/')
def get():
    return RedirectResponse('/cards', status_code=303)

@rt('/cards')
def get():
    card_list = [Li(A(c.front, href=f'/cards/{c.id}'), ' ',
                    Form(Button('Delete'), action=f'/cards/{c.id}/delete', method='post', style='display:inline'))
                 for c in cards()]
    return Titled('Cards', Ul(*card_list), A('New Card', href='/cards/new'))

@rt('/cards/new')
def get():
    return Titled('New Card',
        Form(
            Label('Front:', Input(name='front', required=True)),
            Label('Back:', Input(name='back', required=True)),
            Button('Create'),
            action='/cards/new', method='post'
        )
    )

@rt('/cards/new')
def post(front: str, back: str):
    cards.insert(Card(front=front, back=back, next_review=str(date.today()), interval=1))
    return RedirectResponse('/cards', status_code=303)

@rt('/cards/{id}/delete')
def post(id: int):
    cards.delete(id)
    return RedirectResponse('/cards', status_code=303)

