from fasthtml.common import *
from datetime import date

db = database("cards.db")

_cards = db.t.cards
if _cards not in db.t:
    _cards.create(id=int, front=str, back=str, next_review=str, interval=int, pk="id")

Card = _cards.dataclass()


class TableWrapper:
    def __init__(self, table):
        self._table = table

    def __str__(self):
        return repr(self._table)

    def __repr__(self):
        return repr(self._table)

    def __getattr__(self, name):
        return getattr(self._table, name)

    def __call__(self, *args, **kwargs):
        return self._table(*args, **kwargs)

    def __iter__(self):
        return iter(self._table)

    def __contains__(self, item):
        return item in self._table


cards = TableWrapper(_cards)

app, rt = fast_app()


@rt("/cards")
def get():
    card_list = [Li(f"{c.front} / {c.back}", " ",
                    Form(Button("Delete"), action=f"/cards/{c.id}/delete", method="post"))
                 for c in cards()]
    return Titled("Cards", Ul(*card_list), A("New Card", href="/cards/new"))


@rt("/cards/new")
def get():
    return Titled("New Card",
                  Form(Input(name="front", placeholder="Front"),
                       Input(name="back", placeholder="Back"),
                       Button("Create"),
                       action="/cards/new", method="post"))


@rt("/cards/new")
def post(front: str, back: str):
    cards.insert(Card(front=front, back=back, next_review=str(date.today()), interval=1))
    return Redirect("/cards")


@rt("/cards/{id}/delete")
def post(id: int):
    cards.delete(id)
    return Redirect("/cards")


if __name__ == "__main__":
    serve(port=5001)

