from dataclasses import dataclass
from datetime import date
from fastlite import database
from fasthtml.common import fast_app, RedirectResponse, Form, Input, Button, Ul, Li, A, H1, H2, Label, Textarea

@dataclass
class Card:
    id: int = None
    front: str = ""
    back: str = ""
    next_review: str = ""
    interval: int = 0

db = database("cards.db")
cards = db.create(Card, pk="id")

app, rt = fast_app()

@rt("/")
def get():
    return RedirectResponse("/cards", status_code=303)

@rt("/cards")
def get():
    card_list = cards()
    items = [Li(A(c.front, href=f"/cards/{c.id}"), " - ", c.back) for c in card_list]
    return H1("Cards"), Ul(*items), A("Add New Card", href="/cards/new")

@rt("/cards/new")
def get():
    return H2("New Card"), Form(
        Label("Front:"), Input(name="front", type="text"),
        Label("Back:"), Textarea(name="back"),
        Button("Create Card", type="submit"),
        action="/cards/new", method="post"
    )

@rt("/cards/new")
def post(front: str, back: str):
    cards.insert(Card(front=front, back=back, next_review=str(date.today()), interval=1))
    return RedirectResponse("/cards", status_code=303)

@rt("/cards/{id}/delete")
def post(id: int):
    cards.delete(id)
    return RedirectResponse("/cards", status_code=303)

