from dataclasses import dataclass
from datetime import date
from fastlite import database
from fasthtml.common import fast_app, RedirectResponse, Form, Input, Button, Ul, Li, A, Titled

@dataclass
class Card:
    id: int
    front: str
    back: str
    next_review: date
    interval: int

db = database("cards.db")
cards = db.create(Card, pk="id")

app, rt = fast_app()

@rt("/")
def get():
    return RedirectResponse("/cards", status_code=303)

@rt("/cards")
def get():
    card_list = list(cards())
    items = [Li(f"{c.front} / {c.back}", " ",
                Form(Button("Delete"), action=f"/cards/{c.id}/delete", method="post"))
             for c in card_list]
    return Titled("Cards",
                  A("New Card", href="/cards/new"),
                  Ul(*items) if items else "No cards yet")

@rt("/cards/new")
def get():
    return Titled("New Card",
                  Form(Input(name="front", placeholder="Front"),
                       Input(name="back", placeholder="Back"),
                       Button("Create"),
                       action="/cards/new", method="post"))

@rt("/cards/new")
def post(front: str, back: str):
    cards.insert(Card(id=None, front=front, back=back, next_review=date.today(), interval=1))
    return RedirectResponse("/cards", status_code=303)

@rt("/cards/{id}/delete")
def post(id: int):
    cards.delete(id)
    return RedirectResponse("/cards", status_code=303)

